'use strict';


describe("初始化数据库", function(){
    let myDb = new LocalDb();

    it("应该得到db属性", function(){
		myDb.should.have.property('db');
	});
});

describe("添加数据", function(){
    let myDb = new LocalDb();

    myDb.set('Users', [{name: 'yika', age: 21}, {name: 'youmei', age: 20}]);
    myDb.set('Time', {hour: 10, minute: 2});
    it("应该成功注入到localStorage", function(){
        myDb.getDb().should.have.property('Users');
        myDb.getDb().should.have.property('Time');
    });
});

describe("获取数据", function(){
    let myDb = new LocalDb();

    it("应该得到yika的age是21", function(){
        myDb.get('Users')[0].age.should.eql(21);
	});
});

describe("清空数据库", function(){
    let myDb = new LocalDb();
    myDb.clean();

    it("this.db应该为{}", function(){
		myDb.getDb().should.eql({});
	});
});